  const dispatch = useDispatch();
  const [openModal, setOpenModal] = useState(false);
  const [todayPay, setTodaypay] = useState([]);
  const [search, setsearch] = useState("");
  const [bankDetails, setBank] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [secondTable, setSecondTable] = useState(true);
  const [lengthof, setLengthof] = useState(0);
  const [page, setPage] = useState(1);
  const loader = useRef(null);
  const [totalpages, setTotalPage] = useState(0);
  useEffect(() => {
    if (totalpages < 12) {
      return;
    }
    // Function to check if the document height is greater than the viewport height
    const isScrollRequired = () => {
      return document.documentElement.scrollHeight > window.innerHeight;
    };

    const options = {
      root: null,
      rootMargin: "20px",
      threshold: 0,
    };

    const observerCallback = (entities) => {
      const target = entities[0];
      if (target.isIntersecting) {
        setPage((prev) => prev + 1);
      }
    };

    const observer = new IntersectionObserver(observerCallback, options);

    // Setup observer if scrolling is required
    const setupObserver = () => {
      if (loader.current && isScrollRequired()) {
        observer.observe(loader.current);
      } else {
        // Use a timeout to retry after some time, in case content or images are still loading
        setTimeout(setupObserver, 500);
      }
    };

    setupObserver();

    return () => {
      if (loader.current) {
        observer.unobserve(loader.current);
      }
    };
  }, []);
  useEffect(() => {
    getAllTodaypayout();
  }, [page]);
  const getAllTodaypayout = () => {
    // dispatch(loadSpinner());
    getAlltodaypayout(page)
      .then((data) => {
        console.log(data?.data?.response, "&&&&&&&&&&&&&&&&&&&&&&&&&&");
        setTodaypay((prev) => [...prev, ...data?.data?.response]);
        setLengthof(data?.data?.response?.length);
        setIsLoading(false);
      })
      .catch((err) => {
        setIsLoading(false);
        console.log(err);
      })
      .finally(() => {
        // dispatch(loadSpinner());
      });
  };

